import { Component } from '@angular/core';

@Component({
  selector: 'app-packages',
  templateUrl: './packages.component.html',
  styleUrls: ['./packages.component.css']
})
export class PackagesComponent {
  travelPackages = [
    {
      destination: 'Bali, Indonesia',
      description: 'Enjoy a relaxing beach vacation in beautiful Bali, known for its vibrant culture and scenic landscapes.',
      price: 1200,
      imageUrl: 'https://via.placeholder.com/300x200' // Replace with actual image URL
    },
    {
      destination: 'Paris, France',
      description: 'Explore the city of love with iconic landmarks like the Eiffel Tower and Louvre Museum.',
      price: 1500,
      imageUrl: 'https://via.placeholder.com/300x200' // Replace with actual image URL
    },
    {
      destination: 'Tokyo, Japan',
      description: 'Discover the perfect blend of modern and traditional in the bustling city of Tokyo.',
      price: 1800,
      imageUrl: 'https://via.placeholder.com/300x200' // Replace with actual image URL
    },
    {
      destination: 'Sydney, Australia',
      description: 'Visit Sydney and enjoy its stunning beaches, famous Opera House, and lively culture.',
      price: 1400,
      imageUrl: 'https://via.placeholder.com/300x200' // Replace with actual image URL
    }
  ];
}
<div class="packages-page">
  <h1 class="page-title">Travel Packages</h1>

  <div class="package-card" *ngFor="let package of travelPackages">
    <img [src]="package.imageUrl" alt="{{ package.destination }}" class="package-image"/>
    <div class="package-details">
      <h2>{{ package.destination }}</h2>
      <p>{{ package.description }}</p>
      <div class="package-price">
        <strong>Price: ${{ package.price }}</strong>
      </div>
      <button class="book-now-btn">Book Now</button>
    </div>
  </div>
</div>
