import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { MyService } from 'src/app/services/my.service';

@Component({
  selector: 'app-mybookings',
  templateUrl: './mybookings.component.html',
  styleUrls: ['./mybookings.component.css']
})
export class MybookingsComponent {
  table;
  bookingForm: any;
  registrationForm = this.fb.group({
    id:[''],
    fromDestination: ['', Validators.required],
    toDestination: ['', Validators.required],

  });
constructor(private getm:MyService,private fb: FormBuilder,private _router:Router){

}
ngOnInit(){
  this.getm.getbookings().subscribe((res)=>{
    this.table=res;
  })
    // this.bookingForm = this.fb.group({
    //   bookingId: ['', [Validators.required]],
    //   fromDestination: ['', [Validators.required]],
    //   toDestination: ['', [Validators.required]]
    // });
}
ondelete(data){
this.getm.deletebooking(data).subscribe((res1)=>{
  if(res1){
alert("Deleted Sucessfully")
  }
  else{
    alert("record not deleted")
  }
});
}
c:boolean=false;
remo=true
updatedetails(data){
  this.getm.updatebooking(data).subscribe((res2)=>{
    if(res2){
      alert("updated sucessfully")
      this.ngOnInit()
      this.remo=false
    }
    else{
      alert("update not sucessfull")
    }
  });
}
  }


<div style="background-color: #7FFFD4; padding: 20px; border-radius: 10px;">
  <table class="table table-striped table-hover table-bordered">
    <thead class="thead-dark">
      <tr>
        <th>Booking Id</th>
        <th>From Destination</th>
        <th>To Destination</th>
        <th>Price (USD)</th>
        <th>Date</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let travel of table">
        <td>{{ travel.id }}</td>
        <td>{{ travel.fromDestination }}</td>
        <td>{{ travel.toDestination }}</td>
        <td>${{ travel.price }}</td>
        <td>{{ travel.date }}</td>
        <td>
          <button type="button" class="btn btn-danger" (click)="ondelete(travel.id)">Delete this booking</button>&nbsp;&nbsp;&nbsp;
          <button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">Update this booking</button>
        </td>
      </tr>
    </tbody>
  </table>
</div>

<div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true" *ngIf="remo">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel">Updated Details</h1>
        <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div class="modal-body">
        <form [formGroup]="registrationForm" (ngSubmit)="updatedetails(registrationForm.value)">
          <div>
            <label for="id">ID:</label>
            <input id="id" type="text" formControlName="id" required class="form-control"/>
          </div>
          <div>
            <label for="fromDestination">From Destination:</label>
            <input id="fromDestination" type="text" formControlName="fromDestination" required class="form-control"/>
          </div>
          <div>
            <label for="toDestination">To Destination:</label>
            <input id="toDestination" type="text" formControlName="toDestination" required class="form-control"/>
          </div>
        </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="submit" class="btn btn-warning">Save changes</button>
      </div>
    </div>
  </div>
</div>
