import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { MyService } from 'src/app/services/my.service';

@Component({
  selector: 'app-mybookings',
  templateUrl: './mybookings.component.html',
  styleUrls: ['./mybookings.component.css']
})
export class MybookingsComponent {
  table;
  bookingForm: any;
  registrationForm = this.fb.group({
    ID:[''],
    fromDestination: ['', Validators.required],
    toDestination: ['', Validators.required],
    price: ['', [Validators.required, Validators.min(1)]],
    date: ['', Validators.required],
    terms: [false, Validators.requiredTrue],
  });
constructor(private getm:MyService,private fb: FormBuilder){

}
ngOnInit(){
  this.getm.getbookings().subscribe((res)=>{
    this.table=res;
  })
    // this.bookingForm = this.fb.group({
    //   bookingId: ['', [Validators.required]],
    //   fromDestination: ['', [Validators.required]],
    //   toDestination: ['', [Validators.required]]
    // });
}
ondelete(data){
this.getm.deletebooking(data).subscribe((res1)=>{
  if(res1){
alert("Deleted Sucessfully")
  }
  else{
    alert("record not deleted")
  }
});
}
c:boolean=false;


updatedetails(data){
  this.registrationForm = this.fb.group({
    ID:[''],
    fromDestination: ['', Validators.required],
    toDestination: ['', Validators.required],
    price: ['', [Validators.required, Validators.min(1)]],
    date: ['', Validators.required],
    terms: [false, Validators.requiredTrue],
  });
  this.getm.updatebooking(data);

}
  }


<div style="background-color: aquamarine;">
<table class="table table-striped table-hover table-bordered">
  <thead class="thead-dark">
    <tr>
      <th>Booking Id</th>
      <th>From Destination</th>
      <th>To Destination</th>
      <th>Price (USD)</th>
      <th>Date</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let travel of table">
      <td>{{ travel.id }}</td>
      <td>{{ travel.fromDestination }}</td>
      <td>{{ travel.toDestination }}</td>
      <td>${{ travel.price }}</td>
      <td>{{ travel.date }}</td>
      <td><button type="button" class="btn btn-danger" (click)="ondelete(travel.id)" >Delete this booking</button></td>
      <td><button type="button" class="btn btn-danger" data-bs-toggle="modal" data-bs-target="#exampleModal">Update this booking</button></td>
    </tr>
  </tbody>
</table>



<!-- Modal -->


 <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <h1 class="modal-title fs-5" id="exampleModalLabel"> Updated details</h1>
      <div class="modal-body">
        <form [formGroup]="registrationForm" (ngSubmit)="updatedetails(registrationForm)">
          <div>
            <label for="fromDestination">ID:</label>
            <input id="fromDestination" type="text" formControlName="fromDestination" required />
          </div>
            <div>
              <label for="fromDestination">From Destination:</label>
              <input id="fromDestination" type="text" formControlName="fromDestination" required />
            </div>

            <div>
              <label for="toDestination">To Destination:</label>
              <input id="toDestination" type="text" formControlName="toDestination" required />
            </div>

            <div>
              <label for="price">Price:</label>
              <input id="price" type="number" formControlName="price" required />
            </div>

            <div>
              <label for="date">Date:</label>
              <input id="date" type="date" formControlName="date" required />
            </div>

            <div>
              <label for="terms">
                <input id="terms" type="checkbox" formControlName="terms" />
                I accept the terms and conditions
              </label>
            </div>
          </form>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>
